import React, { Component } from "react";
import { withRouter } from "react-router-dom"; // Import withRouter to access the history object
import './login.scss';

class Login extends Component {
  state = {
    username: "",
    password: "",
  };

  handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleLogin = () => {
    console.log("Login button clicked");
    const { username, password } = this.state;

    // Implement a simple login logic (e.g., check if username and password are not empty)
    if (username.trim() !== "" && password.trim() !== "") {
      // Simulate a successful login by setting a flag or storing a token (you would typically make an API request here)
      // For simplicity, we'll use a local storage flag.
      localStorage.setItem("isLoggedIn", "true");
      console.log("Redirecting to /tasks/new"); // Add this line


      // Use the history object to navigate to the TaskForm component
      this.props.history.push("/tasks/new");
    } else {
      // Handle login failure or show an error message
      alert("Login failed. Please provide valid credentials.");
    }
  };

  render() {
    return (
      <div>
        <h1>Login</h1>
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={this.state.username}
          onChange={this.handleInputChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={this.state.password}
          onChange={this.handleInputChange}
        />
        <button onClick={this.handleLogin}>Login</button>
      </div>
    );
  }
}

export default withRouter(Login);
