import React, { Component } from "react";
import axios from "axios";
//eslint-disable-next-line
import PropTypes from "prop-types";

class TaskList extends Component {
  state = {
    tasks: [],
    isLoading: true,
    error: null,
  };

  componentDidMount() {
    axios.get("YOUR_API_ENDPOINT/tasks")
      .then((response) => {
        this.setState({ tasks: response.data, isLoading: false });
      })
      .catch((error) => {
        if (error.response && error.response.status === 404) {
          this.setState({ error: "Resource not found", isLoading: false });
        } else {
          this.setState({ error: "An error occurred while fetching data", isLoading: false });
        }
      });
  }


  render() {
    const { tasks, isLoading, error } = this.state;

    if (isLoading) {
      return <div>Loading...</div>;
    }

    if (error) {
      return <div>Error: {error.message}</div>;
    }

    return (
      <div>
        <h1>Task List</h1>
        <ul>
          {tasks.map((task) => (
            <li key={task.id}>{task.title}</li>
          ))}
        </ul>
      </div>
    );
  }
}

TaskList.propTypes = {
  // Define propTypes if you have any
};

export default TaskList;
