import React from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import TaskList from "./Components/TaskList";
import TaskForm from "./Components/TaskForm";
import UserManagement from "./Components/UserManagement"; // Import the UserManagement component
import Login from "./Components/Login";

function App() {
  return (
    <Router>
      <Switch>
        <Route path="/" exact component={Login} />
        <Route path="/tasks" exact component={TaskList} />
        <Route path="/tasks/new" exact component={TaskForm} />
        <Route path="/users" exact component={UserManagement} /> {/* Add this route for User Management */}
      </Switch>
    </Router>
  );
}

export default App;
